<?php if (!defined('THINK_PATH')) exit(); if($new): ?><ul class="index_news">
    <?php if(is_array($new)): $i = 0; $__LIST__ = array_slice($new,0,5,true);if( count($__LIST__)==0 ) : echo "" ;else: foreach($__LIST__ as $key=>$vo): $mod = ($i % 2 );++$i;?><li><a href="<?php echo W('Href',array('url'=>$vo['url'],'id'=>$vo['id'],'type'=>'new','lang'=>'c'));?>" title="<?php echo ($vo["title"]); ?>"><?php echo (mb_substr($vo["title"],0,50,'utf-8')); ?></a><span class='news_time'><?php echo (date('Y-m-d',$vo["time"])); ?></span></li><?php endforeach; endif; else: echo "" ;endif; ?>
  </ul><?php endif; ?>